node -e "const fs=require('fs');const p=require('path');const {execSync}=require('child_process');const rtfPath=p.join('node_modules','@react-three','fiber','package.json');if(fs.existsSync(rtfPath)){const pkg=JSON.parse(fs.readFileSync(rtfPath));pkg.dependencies['react-reconciler']='^0.29.2';pkg.dependencies['scheduler']='^0.23.2';fs.writeFileSync(rtfPath,JSON.stringify(pkg,null,2));try{execSync('npm install react-reconciler@0.29.2 scheduler@0.23.2',{cwd:p.join('node_modules','@react-three','fiber'),stdio:'ignore'});}catch(e){}}const dreiStatsPath=p.join('node_modules','@react-three','drei','core','Stats.js');if(fs.existsSync(dreiStatsPath)){let content=fs.readFileSync(dreiStatsPath,'utf-8');if(content.includes('import StatsImpl from')&&!content.includes('Patched:')){const patch='// Patched: Load stats.js UMD module directly
let StatsImpl=null;
if(typeof window!=='undefined'){
  if(window.Stats){StatsImpl=window.Stats;}
  else{
    const script=document.createElement('script');
    script.src='/node_modules/stats.js/build/stats.min.js';
    script.type='text/javascript';
    script.async=false;
    document.head.appendChild(script);
    setTimeout(()=>{StatsImpl=window.Stats;},0);
  }
}';content=content.replace(/import StatsImpl from ['"].*stats.*['"];/,patch);const useEffectfulStateImport=content.match(/import.*useEffectfulState.*from/);if(useEffectfulStateImport){content=content.replace(/const stats = useEffectfulState\(\(\) => new StatsImpl\(\), \[\]\);/,`const stats = useEffectfulState(() => {
    if (!StatsImpl && typeof window !== 'undefined' && window.Stats) {
      StatsImpl = window.Stats;
    }
    return StatsImpl ? new StatsImpl() : null;
  }, []);`);}fs.writeFileSync(dreiStatsPath,content);}}"
